import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

}
<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no">
    <title>Login Page</title>

    <!-- Bootstrap 4 CDN -->
    <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.1.3/css/bootstrap.min.css">

    <!-- Fontawesome CDN -->
    <link rel="stylesheet" href="https://use.fontawesome.com/releases/v5.3.1/css/all.css">

    <!-- Custom styles -->
    <link rel="stylesheet" type="text/css" href="login.component.css">

    <!-- Custom tooltip styles -->
    <style>
        .tooltip-custom {
            position: relative;
            display: inline-block;
        }

        .tooltip-custom .tooltiptext {
            visibility: hidden;
            width: 250px;
            background-color: #6c757d;
            color: #fff;
            text-align: center;
            border-radius: 6px;
            padding: 5px;
            position: absolute;
            z-index: 1;
            bottom: 125%; /* Position the tooltip above the text */
            left: 50%;
            margin-left: -125px;
            opacity: 0;
            transition: opacity 0.3s;
        }

        .tooltip-custom .tooltiptext::after {
            content: "";
            position: absolute;
            top: 100%;
            left: 50%;
            margin-left: -5px;
            border-width: 5px;
            border-style: solid;
            border-color: #6c757d transparent transparent transparent;
        }

        .tooltip-custom:hover .tooltiptext {
            visibility: visible;
            opacity: 1;
        }
    </style>
</head>
<body>
<div class="container">
    <div class="d-flex justify-content-center h-100">
        <div class="card">
            <div class="card-header">
                <h3>Iniciar sesión</h3>
            </div>
            <div class="card-body">
                <form id="loginForm">
                    <div class="input-group form-group tooltip-custom">
                        <div class="input-group-prepend">
                            <span class="input-group-text"><i class="fas fa-user"></i></span>
                        </div>
                        <input type="text" class="form-control" placeholder="Usuario" name="username" id="username" autocomplete="username" required>
                        <span class="tooltiptext">El usuario debe tener entre 4 y 15 caracteres, solo letras y números.</span>
                    </div>
                    <div class="input-group form-group tooltip-custom">
                        <div class="input-group-prepend">
                            <span class="input-group-text"><i class="fas fa-key"></i></span>
                        </div>
                        <input type="password" class="form-control" placeholder="Contraseña" name="password" id="password" autocomplete="current-password" required>
                        <span class="tooltiptext">Tu contraseña debe tener al menos 8 caracteres, incluyendo una letra mayúscula, una letra minúscula, un número y un carácter especial.</span>
                    </div>
                    <div class="row align-items-center remember">
                        <input type="checkbox" name="remember">Recordarme
                    </div>
                    <div class="form-group">
                        <button type="submit" class="btn float-right login_btn">Iniciar sesión</button>
                    </div>
                </form>
            </div>
            <div class="card-footer">
                <div class="d-flex justify-content-center links">
                    ¿No tienes una cuenta?<a href="#">Regístrate</a>
                </div>
                <div class="d-flex justify-content-center">
                    <a href="#">¿Olvidaste tu contraseña?</a>
                </div>
            </div>
        </div>
    </div>
</div>
<script>
$(document).ready(function(){
    $('#loginForm').on('submit', function(e) {
        var isValid = true;
        var username = $('#username').val();
        var password = $('#password').val();
        var usernamePattern = /^[a-zA-Z0-9]{4,15}$/;
        var passwordPattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

        // Check required fields
        $(this).find('input[required]').each(function() {
            if ($(this).val() === '') {
                isValid = false;
                $(this).addClass('is-invalid');
            } else {
                $(this).removeClass('is-invalid');
            }
        });

        // Check username pattern
        if (!usernamePattern.test(username)) {
            isValid = false;
            $('#username').addClass('is-invalid');
        } else {
            $('#username').removeClass('is-invalid');
        }

        // Check password pattern
        if (!passwordPattern.test(password)) {
            isValid = false;
            $('#password').addClass('is-invalid');
        } else {
            $('#password').removeClass('is-invalid');
        }

        if (!isValid) {
            e.preventDefault();
        }
    });
});
</script>
</body>
</html>
